import React from 'react';
import axios from 'axios';
import { newExpression } from '@babel/types';

class addShow extends React.Component {
  constructor(props){
    super(props);

    this.state = {Name:'',
                  YearOfR:'',
                Poster:'',
              Base64Image:''};

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleYearOfRChange = this.handleYearOfRChange.bind(this);
    this.handlePoster = this.handlePoster.bind(this);
    this.handleImageChange = this.handleImageChange.bind(this);
  }
  //functions for adding to database
  handleNameChange(e){
    this.setState({Name: e.target.value});
  }

  handleYearOfRChange(e){
    this.setState({YearOfR: e.target.value});
  }

  handlePoster(e){
    this.setState({Poster: e.target.value});
  }
  // file upload 
  getBase64(file, cb) {
    let reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = function () {
        cb(reader.result)
    };
    reader.onerror = function (error) {
        console.log('Error: ', error);
    };
}

  handleImageChange(e){
    alert(e.target.files[0]);
    this.getBase64(e.target.files[0], (base64) =>{
        this.setState({Base64Image:base64});
    })
  }

  handleSubmit(e){
    alert(this.state.Name+ "      " + this.state.YearOfR 
    +"       "+ this.state.Poster);
    e.preventDefault();
    
    const newShow = {
                  Name: this.state.Name,
                  YearOfR: this.state.YearOfR,
                  poster: this.state.Poster
                };
          axios.post('http://localhost:4000/api/shows',newShow) 
          .then()
          .catch();
          

          this.setState({Name:'',
                  YearOfR:'',
                Poster:''});    
  }

  render() {
    return (
      <div>
        <font color="white"><label><h1>Add a show to the What's New section</h1></label></font> 
        <form onSubmit={this.handleSubmit}>
        <div className='form-group'>
        <font color="white"><label>Show Name</label></font>
          <input
          type='text'
          className='form-control'
          value={this.state.Name}
          onChange={this.handleNameChange}
          ></input>
        </div>
        <div className='form-group'>
        <font color="white"><label>Show Year Of Release</label></font>
          <input
          type='text'
          className='form-control'
          value={this.state.YearOfR}
          onChange={this.handleYearOfRChange}
          ></input>
        </div>
        <div className='form-group'>
        <font color="white"><label>Show Poster Url</label></font>
          <textarea
          row='3'
          className='form-control'
          value={this.state.Poster}
          onChange={this.handlePoster}
          ></textarea>
        </div>
        <div>
          <font color="white"><label>Upload Poster From Your PC</label></font>
          <input
          type='file'
          className='form-control'
          onChange={this.handleImageChange}
          ></input>

        </div>
        <div>
          <input
          type="submit"
          value="Add Show">
          </input>
        </div>
        </form>
        <img src={this.state.Base64Image}></img>
        <br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br>
      </div>
      
    );
  }
}

export default addShow;
